mapper.select:
		SELECT
	    class.*, classcouese_time,classcouese_address
        FROM
	    class,class_course_info
        WHERE
	    class.class_no IN (
		SELECT
			class_no
		FROM
			class_course_info
		WHERE
			teacher_no = #{tc.teacher_no}
		AND course_no = #{tc.course_no}
	    )
        AND teacher_no = #{tc.teacher_no}
        AND course_no = #{tc.course_no}
